mapper.select:
SELECT u_id, u_name, phone, email, wallet
FROM flash_sale.Users
WHERE u_id = #{userId};

SELECT u_id, u_name, phone, email, wallet
FROM flash_sale.Users
WHERE u_name = #{userName};

SELECT u_id, u_name, phone, email, wallet
FROM flash_sale.Users
WHERE phone = #{userPhone};

SELECT u_id, u_name, phone, email, wallet
FROM flash_sale.Users
WHERE u_name = #{userName} AND phone = #{userPhone};

mapper.update:
UPDATE flash_sale.Users
SET u_name = #{u_name},
    phone = #{phone},
    email = #{email},
    wallet = #{wallet}
WHERE u_id = #{u_id};
